using System;using System.Collections.Generic;using System.Linq;using System.Text;using Microsoft.CodeAnalysis.CSharp.Syntax;using TypealizR.Extensions;namespace TypealizR.Core;internal class MemberName{    private readonly string name;    public MemberName(string value)    {        name = new string(            value                .Replace(" ", "_")                .Trim('_')                .Select((x, i) => x.IsValidInIdentifier(i == 0) ? x : ' ')                .ToArray()        )        .Replace(" ", "")        .Trim('_');    }    public static implicit operator string (MemberName that) => that.name;    public override string ToString() => name;}